import React, { Component } from 'react';

import './App.css';
import * as io from 'socket.io-client';

const socket = io.connect('http://localhost:5000');

class App extends Component {
  constructor(props){
    super(props);

    this.state = {
      messages: [],
      input: '',
      id: '',
      name: '',
      location: {latitude: '', longitude: ''},
      locationInterval: null

    };
    this.handleSend = this.handleSend.bind(this);
  }

  componentDidMount() {
    let current = this;
    socket.on('chat', function(msg){
      current.setState({messages: [...current.state.messages, msg]});
    });

    socket.on('on_connection', function(userInfo){
      current.setState({id: userInfo.id, name: userInfo.name});
    });

  }

  beginLocationEmit() {
    this.setState({locationInterval: setInterval(function(){
      socket.emit('location_update', {id: this.state.id, location: this.state.location});
    }, 10000

    )});
  }
  handleSend(event) {
    event.preventDefault();

    console.log(this.state.input);
    socket.emit('chat', this.state.input);
    this.setState({input: ''});

  }

render() {
  let current = this;
  if ("geolocation" in navigator){
    navigator.geolocation.getCurrentPosition(function(position) {
      current.setState({location: {
        latitude: position.coords.latitude,
        longitude: position.coords.longitude
      }
    });
    });
  }
    let messageList = this.state.messages.map((x, index) => {
      return (
        <div key={index}> {x} </div>
      );
    });
    return (
      <div id="container">
        <div id="message-container">
          {messageList}
        </div>
        <form id='message-form'>
          <input id='message-input' type='text' value={this.state.input} onChange={e => this.setState({input: e.target.value})} />
          <input id='message-submit' type='submit' value='Send' onClick={this.handleSend} />
        </form>
      </div>
    );
  }
}

export default App;
