import React, { Component } from 'react';

import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Button, InputGroup, InputGroupText, InputGroupAddon,
    Collapse, CardBody, Card, Input, Form, Navbar,
    NavbarToggler, NavbarBrand, Nav, NavItem, NavLink,
    Dropdown, DropdownMenu, DropdownToggle, Modal, ModalHeader,
    ModalBody, ModalFooter} from 'reactstrap';
import * as io from 'socket.io-client';

const socket = io.connect('http://localhost:5000');

class App extends Component {
  constructor(props){
    super(props);

    this.state = {
      messages: [],
      input: '',
      id: '',
      name: '',
      userNames: [],
      location: {latitude: '', longitude: ''},
      locationInterval: null,
      collapse: false,
      dropdownOpen: false,
      modal: false

    };
    this.handleSend = this.handleSend.bind(this);
    this.beginLocationEmit = this.beginLocationEmit.bind(this);
    this.toggleNavbar = this.toggleNavbar.bind(this);
    this.toggle = this.toggle.bind(this);
    this.modalToggle = this.modalToggle.bind(this);
  }
  componentWillUpdate(){
    var elem = document.getElementById('message-window');
    elem.scrollTop = elem.scrollHeight;
  }
  componentDidMount() {
    let current = this;
    socket.on('chat', function(msg){
      current.setState({messages: [...current.state.messages, msg]});
    });

    socket.on('on_connection', function(userInfo){
      current.setState({id: userInfo.id, name: userInfo.name}, current.beginLocationEmit());
    });

    socket.on('name_update', function(names){
      current.setState({userNames: names});
    });

  }

  beginLocationEmit() {
    let current = this;
    this.setState({locationInterval: setInterval(function(){
      socket.emit('location_update', {id: current.state.id, location: current.state.location});
    }, 10000

    )});
  }
  handleSend(event) {
    event.preventDefault();
    socket.emit('chat', this.state.input);
    this.setState({input: ''});

  }
  toggle() {
    this.setState({
      dropdownOpen: !this.state.dropdownOpen
    });
  }
  toggleNavbar() {
    this.setState({
      collapsed: !this.state.collapsed
    });
  }
  modalToggle() {
    this.setState({modal: !this.state.modal});
  }

render() {
  let current = this;
  if ("geolocation" in navigator){
    navigator.geolocation.getCurrentPosition(function(position) {
      current.setState({location: {
        latitude: position.coords.latitude,
        longitude: position.coords.longitude
      }
    });
    });
  }
    let messageList = this.state.messages.map((x, index) => {
      return (
        <div id="message-item" key={index}> {x} </div>
      );
    });

    let nameList = this.state.userNames.map((x, index) => {
      return (
        <div id="name-item" key={index}> {x} </div>
      );
    });

    return (
      <div id="container">
      <div>
      <Navbar color="faded" light>
        <NavbarBrand href="/" className="mr-auto">RadioChat</NavbarBrand>
        <NavbarToggler onClick={this.toggleNavbar} className="mr-2" />
        <Collapse isOpen={this.state.collapsed} navbar>
          <Nav navbar>
            <NavItem>
              <Dropdown isOpen={this.state.dropdownOpen} toggle={this.modalToggle}>
                <DropdownToggle
                tag="span"
                onClick={this.modalToggle}
                data-toggle="dropdown"
                aria-expanded={this.state.dropdownOpen}
                >
                Change Name
                </DropdownToggle>
                <DropdownMenu>
                  <Button color="danger" onClick={this.modalToggle}>Hello</Button>
                  <Modal isOpen={this.state.modal} toggle={this.modalToggle} className={this.props.className}>
                    <ModalHeader toggle={this.modalToggle}>Modal title</ModalHeader>
                    <ModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                    </ModalBody>
                    <ModalFooter>
            <Button color="primary" onClick={this.modalToggle}>Do Something</Button>{' '}
            <Button color="secondary" onClick={this.modalToggle}>Cancel</Button>
          </ModalFooter>
        </Modal>

                </DropdownMenu>
              </Dropdown>
            </NavItem>
          </Nav>
        </Collapse>
      </Navbar>
    </div>
        <div id="message-container">
          <div id="message-window">
            {messageList}
          </div>
          <div id="user-list">
            {nameList}
          </div>
          <Form>
            <InputGroup id='textInput'>
              <Input value={this.state.input} onChange={e=>this.setState({input: e.target.value})}/>
              <InputGroupAddon addonType="append"><Button type='submit' onClick={this.handleSend}
                color="secondary">Send</Button>
              </InputGroupAddon>
            </InputGroup>
          </Form>
        </div>

      </div>
    );
  }
}

export default App;
