import React, { Component } from 'react';

import logo from './logo.svg';

import './App.css';
import * as io from 'socket.io-client';

const socket = io();

class App extends Component {
  constructor(props){
    super(props);

    this.state = {
      messages: ['cat', 'dog', 'meow', 'woof'],
      input: 'hello'

    };
    this.handleSend = this.handleSend.bind(this);
  }

  componentDidMount() {
    socket.on('chat', function(msg){
      this.setState({messages: [...state.messages, msg]});
    });
  }
  handleSend(event) {
    event.preventDefault();
    console.log(this.state.input);
    socket.emit('chat', this.state.input);
  }

render() {
    let messageList = this.state.messages.map(x => {
      return (
        <li> {x} </li>
      );
    });
    return (
      <div id="container">
        <div id="message-container">
          <ul id="message-list">
            {messageList}
          </ul>
        </div>
        <form id='message-form'>
          <input id='message-input' type='text' value={this.state.input} onChange={e => this.setState({input: e.target.value})} />
          <button id='message-submit' type='button' onClick={this.handleSend}> Send
          </button>
        </form>
      </div>
    );
  }
}

export default App;
